import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CategoryTag from '../CategoryTag/CategoryTag';
import Footer from '../Footer/Footer';
import './detail.css';

const Detail = () => {
    const [productList, setProductList] = useState([]);
    const [product, setProduct] = useState({});
    const [quantity, setQuantity] = useState(1);
    const [subtotal, setSubtotal] = useState(0);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://localhost:8080/products/queryAllProducts');
                setProductList(response.data);
            } catch (error) {
                console.error('Error fetching product data:', error);
                // Handle error if necessary...
            }
        };

        fetchData();
    }, []);

    const handleQuantityChange = (e) => {
        setQuantity(e.target.value);
        // Update subtotal whenever quantity changes
        const newSubtotal = calculateSubtotal(product.price, e.target.value);
        setSubtotal(newSubtotal);
    };

    const calculateSubtotal = (price, quantity) => {
        return price * quantity;
    };

    const handleOrder = async () => {
        try {
            const data = {
                item: product.productId, // 将 product.productId 作为 item
                item_name: product.name, // 将 product.name 作为 item_name
                amount: quantity,
                subtotal: calculateSubtotal(), // 计算 subtotal
            };

            const response = await axios.post('http://localhost:8080/cake/add', data);
            console.log(response.data);
            alert('訂單已提交！');
            // Redirect to product page after successful order submission
            // Navigate function here...
        } catch (error) {
            console.error('Error submitting order:', error);
            alert('訂單提交失敗！');
        }
    };


    const handleProductSelect = (productId) => {
        const selectedProduct = productList.find(product => product.product_id === productId);
        setProduct(selectedProduct);
        // Calculate initial subtotal based on selected product and default quantity
        const newSubtotal = calculateSubtotal(selectedProduct.price, quantity);
        setSubtotal(newSubtotal);
    };

    return (
        <>
            <img src="images/banner.jpg" className="img-responsive" id="banner" alt="" />
            <div id="content" className="container">
                <h3 className="title">| 產品細節<small>Detail</small></h3>
                <div className="row">
                    <div className="col-md-8">
                        <img src={`../../images/${product.img}`} className="img-responsive" alt="" />
                    </div>
                    <div className="col-md-4">
                        <h3>{product.name}</h3>
                        <p>{product.descript}</p>
                        <p>價錢：{product.price}元 / 片</p>
                        <p>分類：
                            {product.category && product.category.map(c => <CategoryTag key={c} tag={c} />)}
                        </p>
                        <form>
                            <div className="form-group">
                                <label htmlFor="number">數量</label>
                                <input type="number" className="form-control" id="number" placeholder="" value={quantity} onChange={handleQuantityChange} />
                            </div>
                            <button type="button" className="btn btn-default" onClick={handleOrder}>我要訂購</button>
                        </form>
                    </div>
                </div>
            </div>
            <Footer />
        </>
    );
};

export default Detail;
