import React, { Component } from 'react'
import Footer from '../Footer/Footer';
import './productList.css';
import axios from 'axios'

export default class QueryAllChat extends Component {
  constructor(props) {
    super(props);
    this.state = {
      deleteId: ''
    };
  }

  handleInputChange = (e) => {
    this.setState({
      deleteId: e.target.value
    });
  };

  handleDelete = () => {
    const { deleteId } = this.state;
    axios.delete(`http://192.168.1.103:8080/cake/delete/${deleteId}`)
      .then(response => {
        // 更新显示的内容，例如重新获取数据并显示
        this.test();
      })
      .catch(error => {
        console.error('删除失败:', error);
      });
  };

  test = () => {
    axios.get("http://192.168.1.103:8080/cake/queryAllCake")
      .then(response => {
        let y = "<table border=1 align='center' width=70%>";
        y += "<tr><th>編號</th><th>商品ID</th><th>數量</th><th>金額</th></tr>";

        let sum = 0;
        for (let i = 0; i < response.data.length; i++) {
          y += "<tr><td>" + response.data[i].id +
            "<td>" + response.data[i].item +
            "<td>" + response.data[i].amount +
            "<td>" + response.data[i].subtotal;

          sum++;
        }
        y += "</table>";
        let s = document.getElementById("show");
        s.innerHTML = y + "<br/>共:" + sum + "筆";
      })
      .catch(error => {
        console.error('获取数据失败:', error);
      });
  };

  render() {
    return (
      <div>
        <img src="images/banner.jpg" className="img-responsive" id="banner" alt="" />
        <button onClick={this.test}>查看訂購內容</button><br/><br/>
        <input type="text" value={this.state.deleteId} onChange={this.handleInputChange} placeholder="輸入要刪除的ID" />
        <button onClick={this.handleDelete}>刪除</button><br/>
      
  
        <div id="show"></div>
        <Footer />
      </div>
    )
  }
}
