import React, { useState, useEffect } from 'react';
import { useParams, useLocation } from "react-router-dom";
import CategoryTag from '../CategoryTag/CategoryTag';
import Footer from '../Footer/Footer';
import './detail.css';
import axios from 'axios';

const Detail = () => {
    let params = useParams();
    // 抓取Route過來的id
    console.log('params',params);

    const [ product, setProduct ] = useState({});
    const handleOrder = async () => {
        try {
            // 構造要傳送的資料
            const data = {
                productId: product.productId,

                // 在這裡可以添加更多要傳送的資料，如使用者相關資訊等等
            };

            // 發送 POST 請求到後端的某個端點，將資料送入資料庫
            const response = await axios.post('http://192.168.1.103:8080/chat/add', data);

            // 處理後端的回應，這裡可以根據後端回應做不同的處理
            console.log(response.data);
            alert('訂單已提交！');
        } catch (error) {
            console.error('發生錯誤：', error);
            alert('訂單提交失敗！');
        }
    };

    useEffect(() => {
        const productList = [ 
            {
                productId: 1,
                name: "鄉村檸檬乳酪塔",
                descript: "採用紐西蘭小鄉村濃郁的乳酪香味，搭配清涼檸檬口感是鄉村中最具引響力的一道甜點。",
                img: "cake01.jpg",
                price: 79,
                category: ["水果", "蛋糕"]
            }, {
                productId: 2,
                name: "精緻手工巧克蛋糕",
                descript: "本店採用歐洲低筋麵粉純手工製作，搭配瑞士金典巧克力碎片，每一口都能被巧克力滋潤，令人著迷且充滿想像的奇幻空間。",
                img: "cake02.jpg",
                price: 69,
                category: ["巧克力", "蛋糕"]
            }, {
                productId: 3,
                name: "限量莓果乳酪蛋糕",
                descript: "自製微酸香甜的草莓醬點綴了藍莓，雙層華麗的享受與令人驚豔的口感絕對讓你幸福美滿。",
                img: "cake03.jpg",
                price: 89,
                category: ["水果", "蛋糕"]
            }, {
                productId: 4,
                name: "法式藍莓蛋糕",
                descript: "新鮮藍莓的清香在嘴裡蔓延，甜而不膩的獨家配方，每一口都是幸福的感受。",
                img: "cake04.jpg",
                price: 49,
                category: ["水果", "蛋糕"]
            }
        ];

        let product = productList.find(p => p.productId === Number(params.id));
        console.log('product', product);
        setProduct(product);

    },[]);
    if (product === null) {
        return <div>Loading...</div>;
    }

    return (
        <>
            <img src="images/banner.jpg" className="img-responsive" id="banner" alt="" />
            <div id="content" className="container">
                <h3 className="title">| 產品細節<small>Detail</small></h3>
                <div className="row">
                    <div className="col-md-8">
                    <img src={`../../images/${product.img}`} className="img-responsive" alt="" />
                    </div>
                    <div className="col-md-4">
                        <h3>{product.name}</h3>
                        <p>{product.descript}</p>
                        <p>價錢：{product.price}元 / 片</p>
                        <p>分類：
                            {product.category && product.category.map(c => <CategoryTag key={c} tag={c} />)}
                        </p>
                        <form>
                            <div className="form-group">
                                <label htmlFor="number">數量</label>
                                <input type="text" className="form-control" id="number" placeholder="" />
                            </div>
                            <button type="button" className="btn btn-default" onClick={handleOrder}>我要訂購</button>
                        </form>
                    </div>
                </div>
            </div>
            <Footer />
        </>
    );
}

export default Detail;
