import React, { Component } from 'react';
import Footer from '../Footer/Footer';
import './productList.css';
import axios from 'axios';

export default class QueryAllChat extends Component {
  constructor(props) {
    super(props);
    this.state = {
      deleteId: ''
    };
  }

  handleInputChange = (e) => {
    this.setState({
      deleteId: e.target.value
    });
  };

  handleDelete = () => {
    const { deleteId } = this.state;
    axios.delete(`http://192.168.1.103:8080/cake/delete/${deleteId}`)
      .then(response => {
        // 更新显示的内容，例如重新获取数据并显示
        this.test();
      })
      .catch(error => {
        console.error('删除失败:', error);
      });
  };

  test = () => {
    axios.get("http://192.168.1.103:8080/cake/queryAllCake")
      .then(response => {
        let y = "<table border=1 align='center' width=70%>";
        y += "<tr><th>訂單編號</th><th>商品代號</th><th>商品品名</th><th>數量</th><th>金額</th></tr>";

        let sum = 0;
        for (let i = 0; i < response.data.length; i++) {

          const amount = response.data[i].amount.toLocaleString(); // 數量加上千分位逗號
          const subtotal = response.data[i].subtotal.toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 }); // 金額保留兩位小數，並加上千分位逗號
          
          y += "<tr><td>" + response.data[i].id +
            "<td>" + response.data[i].item +
            "<td>" + response.data[i].itemName +
            "<td class='right-align'>" + amount +
            "<td class='right-align'>" + subtotal + " NTD";


          sum++;
        }
        y += "</table>";
        let s = document.getElementById("show");
        s.innerHTML = y + "<br/>共:" + sum + "筆";
      })
      .catch(error => {
        console.error('获取数据失败:', error);
      });
  };

  render() {
    return (
      <div>
        <img src="images/banner.jpg" className="img-responsive" id="banner" alt="" /><br/>
        <ul className="nav nav-tabs" id="myTab">
          <li className="nav-item"><a className="nav-link active" data-bs-toggle="tab" href="#query">查詢訂單</a></li>
          <li className="nav-item"><a className="nav-link" data-bs-toggle="tab" href="#delete">刪除訂單</a></li>
        </ul>
        
        <div className="tab-content" id="myTabContent">
          <div className="tab-pane fade show active" id="query">
            <div>
              <button className="custom-button" onClick={this.test}>查看訂購內容</button><br/><br/>
              <div id="show"></div>
            </div>
          </div>
          <div className="tab-pane fade" id="delete">
            <div>
              <input type="text" value={this.state.deleteId} onChange={this.handleInputChange} placeholder="輸入要刪除的訂單ID" />
              <button className="custom-button" onClick={this.handleDelete}>刪除</button><br/><br/>
            </div>
          </div>
        </div>
        <Footer />
      </div>
    );
  }
}
