import React, { useState, useEffect } from 'react';
import Footer from '../Footer/Footer';
import './productList.css';
import axios from 'axios';

const QueryAllChat = () => {
  const [deleteId, setDeleteId] = useState('');
  const [updateId, setUpdateId] = useState('');
  const [updateItem, setUpdateItem] = useState('');
  const [updateQuantity, setUpdateQuantity] = useState('');
  const [showDeleteControls, setShowDeleteControls] = useState(false);
  const [showUpdateControls, setShowUpdateControls] = useState(false);
  const [queryResult, setQueryResult] = useState([]);

  useEffect(() => {
    test();
  }, []); // 在組件首次渲染時獲取數據

  const handleInputChange = (e) => {
    setDeleteId(e.target.value);
  };

  const handleDeleteClick = () => {
    setShowDeleteControls(true);
    test();
  };

  const handleDelete = () => {
    axios.delete(`http://192.168.1.103:8080/cake/delete/${deleteId}`)
      .then(response => {
        test();
        setDeleteId('');
      })
      .catch(error => {
        console.error('删除失败:', error);
      });
  };

  const test = () => {
    axios.get("http://192.168.1.103:8080/cake/queryAllCake")
      .then(response => {
        setQueryResult(response.data);
      })
      .catch(error => {
        console.error('获取数据失败:', error);
      });
  };

  const handleQueryClick = () => {
    setShowDeleteControls(false);
    test();
  };

  const handleUpdateClick = () => {
    setShowUpdateControls(true);
  };

  const handleUpdateInputChange = (e) => {
    const { name, value } = e.target;
    switch(name) {
      case 'updateId':
        setUpdateId(value);
        break;
      case 'updateItem':
        setUpdateItem(value);
        break;
      case 'updateQuantity':
        setUpdateQuantity(value);
        break;
      default:
        break;
    }
  };

  const handleUpdate = () => {
    const data = {
      item: updateItem,
      amount: updateQuantity
    };

    axios.put(`http://192.168.1.103:8080/cake/update/${updateId}`, data)
      .then(response => {
        test();
        setUpdateId('');
        setUpdateItem('');
        setUpdateQuantity('');
        setShowUpdateControls(false);
      })
      .catch(error => {
        console.error('更新訂單失敗:', error);
      });
  };

  return (
    <div>
      <img src="images/banner.jpg" className="img-responsive" id="banner" alt="" /><br/>
      <ul className="nav nav-tabs">
        <li className="nav-item">
          <button className="custom-button" onClick={handleQueryClick}>查看訂單</button>
        </li>
        <li className="nav-item">
          <button className="custom-button" onClick={handleDeleteClick}>刪除</button>
        </li>
        <li className="nav-item">
          <button className="custom-button" onClick={handleUpdateClick}>修改</button>
        </li>
      </ul>
      {showDeleteControls && (
        <div>
          <input type="text" value={deleteId} onChange={handleInputChange} placeholder="輸入要刪除的訂單ID" />
          <button onClick={handleDelete}>確認刪除</button>
        </div>
      )}
      {showUpdateControls && (
        <div>
          <input type="text" name="updateId" value={updateId} onChange={handleUpdateInputChange} placeholder="輸入要修改的訂單ID" />
          <input type="text" name="updateItem" value={updateItem} onChange={handleUpdateInputChange} placeholder="輸入要修改的商品" />
          <input type="text" name="updateQuantity" value={updateQuantity} onChange={handleUpdateInputChange} placeholder="輸入修改後的數量" />
          <button onClick={handleUpdate}>確認修改</button>
        </div>
      )}
      {queryResult.length > 0 && (
        <div id="show">
          <table border="1" align="center" width="70%">
            <thead>
              <tr>
                <th>訂單編號</th>
                <th>商品代號</th>
                <th>商品品名</th>
                <th>數量</th>
                <th>金額</th>
              </tr>
            </thead>
            <tbody>
              {queryResult.map(item => (
                <tr key={item.id}>
                  <td>{item.id}</td>
                  <td>{item.item}</td>
                  <td>{item.itemName}</td>
                  <td className="right-align">{item.amount ? item.amount.toLocaleString() : ""}</td>
                  <td className="right-align">{item.subtotal ? item.subtotal.toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 }) + " NTD" : ""}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <br/>共:{queryResult.length}筆
        </div>
      )}
      <Footer />
    </div>
  );
}

export default QueryAllChat;
