import React, { Component } from 'react'
import Footer from '../Footer/Footer';
import './productList.css';
import axios from 'axios'

export default class QueryAllChat extends Component {
  constructor(props) {
    super(props);
    this.state = {
      deleteId: '',
      showDeleteControls: false, // 添加一個標誌位來控制刪除相關元素的顯示
      queryResult: []
    };
  }

  handleInputChange = (e) => {
    this.setState({
      deleteId: e.target.value
    });
  };

  handleDeleteClick = () => {
    this.setState({
      showDeleteControls: true, // 點擊刪除時設置標誌位為true，顯示相關元素
      queryResult: [] // 清空查詢結果
    });
  };

  handleDelete = () => {
    const { deleteId } = this.state;
    axios.delete(`http://192.168.1.103:8080/cake/delete/${deleteId}`)
      .then(response => {
        this.test();
      })
      .catch(error => {
        console.error('删除失败:', error);
      });
  };

  test = () => {
    axios.get("http://192.168.1.103:8080/cake/queryAllCake")
      .then(response => {
        this.setState({
          queryResult: response.data
        });
      })
      .catch(error => {
        console.error('获取数据失败:', error);
      });
  };

  render() {
    const { showDeleteControls, queryResult } = this.state;
    return (
      <div>
        <img src="images/banner.jpg" className="img-responsive" id="banner" alt="" /><br/>
        <ul className="nav nav-tabs">
          <li className="nav-item">
            <button className="nav-link active" onClick={this.test}>查看訂單</button>
          </li>
          <li className="nav-item">
            <button className="nav-link" onClick={this.handleDeleteClick}>刪除</button>
          </li>
        </ul>
        {showDeleteControls && ( // 條件渲染刪除相關元素
          <div>
            <input type="text" value={this.state.deleteId} onChange={this.handleInputChange} placeholder="輸入要刪除的訂單ID" />
            <button onClick={this.handleDelete}>確認刪除</button>
          </div>
        )}
        <div id="show">
          <table border="1" align="center" width="70%">
            <thead>
              <tr>
                <th>訂單編號</th>
                <th>商品代號</th>
                <th>商品品名</th>
                <th>數量</th>
                <th>金額</th>
              </tr>
            </thead>
            <tbody>
              {queryResult.map(item => (
                <tr key={item.id}>
                  <td>{item.id}</td>
                  <td>{item.item}</td>
                  <td>{item.itemName}</td>
                  <td className="right-align">{item.amount.toLocaleString()}</td>
                  <td className="right-align">{item.subtotal.toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 })} NTD</td>
                </tr>
              ))}
            </tbody>
          </table>
          <br/>共:{queryResult.length}筆
        </div>
        <Footer />
      </div>
    )
  }
}
