import React, { Component } from 'react';
import Footer from '../Footer/Footer';
import './productList.css';
import axios from 'axios';

export default class QueryAllChat extends Component {
  constructor(props) {
    super(props);
    this.state = {
      deleteId: '',
      showDeleteControls: false, // 控制刪除相關元素的顯示
      queryResult: [],
      updateOrderId: '', // 追蹤要修改的訂單編號
      updateItem: '', // 追蹤要修改的商品
      updateQuantity: '', // 追蹤要修改的數量
    };
  }

  handleInputChange = (e) => {
    this.setState({
      deleteId: e.target.value
    });
  };

  handleDeleteClick = () => {
    this.setState({
      showDeleteControls: true, // 點擊刪除時設置為true，顯示相關元素
    });
    this.test();
  };

  handleDelete = () => {
    const { deleteId } = this.state;
    axios.delete(`http://192.168.1.103:8080/cake/delete/${deleteId}`)
      .then(response => {
        this.test();
        this.setState({ deleteId: '' }); // 清空文字框的內容
      })
      .catch(error => {
        console.error('删除失败:', error);
      });
  };

  handleUpdateClick = () => {
    this.setState({
      showDeleteControls: false, // 重置為false，隱藏刪除相關元素
      updateOrderId: '', // 清空要修改的訂單編號
      updateItem: '', // 清空要修改的商品
      updateQuantity: '', // 清空要修改的數量
    });
  };

  handleUpdateItemChange = (e) => {
    this.setState({
      updateItem: e.target.value
    });
  };

  handleUpdateQuantityChange = (e) => {
    this.setState({
      updateQuantity: e.target.value
    });
  };

  handleUpdateOrder = () => {
    const { updateOrderId, updateItem, updateQuantity } = this.state;
    axios.put(`http://192.168.1.103:8080/cake/update/${updateOrderId}`, {
      item: updateItem,
      amount: updateQuantity
    })
      .then(response => {
        // 更新訂單後的操作
        console.log('訂單更新成功:', response.data);
        // 重新載入訂單列表或其他操作
      })
      .catch(error => {
        console.error('訂單更新失敗:', error);
      });
  };

  test = () => {
    axios.get("http://192.168.1.103:8080/cake/queryAllCake")
      .then(response => {
        this.setState({
          queryResult: response.data
        });
      })
      .catch(error => {
        console.error('获取数据失败:', error);
      });
  };

  handleQueryClick = () => {
    this.setState({
      showDeleteControls: false, // 重置為false，隱藏相關元素
    });
    this.test();
  };

  render() {
    const { showDeleteControls, queryResult, updateItem, updateQuantity } = this.state;
    return (
      <div>
        <img src="images/banner.jpg" className="img-responsive" id="banner" alt="" /><br/>
        <ul className="nav nav-tabs">
          <li className="nav-item">
            <button className="custom-button" onClick={this.handleQueryClick}>查看訂單</button>
          </li>
          <li className="nav-item">
            <button className="custom-button" onClick={this.handleDeleteClick}>刪除</button>
          </li>
          <li className="nav-item">
            <button className="custom-button" onClick={this.handleUpdateClick}>修改</button>
          </li>
        </ul>
        {showDeleteControls && (
          <div>
            <input type="text" value={this.state.deleteId} onChange={this.handleInputChange} placeholder="輸入要刪除的訂單ID" />
            <button onClick={this.handleDelete}>確認刪除</button>
          </div>
        )}
        {updateOrderId && (
          <div>
            <input type="text" value={updateItem} onChange={this.handleUpdateItemChange} placeholder="輸入要修改的商品" />
            <input type="text" value={updateQuantity} onChange={this.handleUpdateQuantityChange} placeholder="輸入要修改的數量" />
            <button onClick={this.handleUpdateOrder}>確認修改</button>
          </div>
        )}
        {queryResult.length > 0 && (
          <div id="show">
            <table border="1" align="center" width="70%">
              <thead>
                <tr>
                  <th>訂單編號</th>
                  <th>商品代號</th>
                  <th>商品品名</th>
                  <th>數量</th>
                  <th>金額</th>
                </tr>
              </thead>
              <tbody>
                {queryResult.map(item => (
                  <tr key={item.id}>
                    <td>{item.id}</td>
                    <td>{item.item}</td>
                    <td>{item.itemName}</td>
                    <td className="right-align">{item.amount ? item.amount.toLocaleString() : ""}</td>
                    <td className="right-align">{item.subtotal ? item.subtotal.toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 }) + " NTD" : ""}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <br/>共:{queryResult.length}筆
          </div>
        )}
        <Footer />
      </div>
    )
  }
}
